mapper.sql:
where 1=1

	
		and IMAGE.ID=#{id}
	
	
		and IMAGE.VERSION like CONCAT('%',#{version},'%') escape '^'
	
	
		and IMAGE.DESCN=#{descn}
	
	
		and IMAGE.AREA_ID=#{areaId}
	
	
		and IMAGE.HCLUSTER_ID=#{hclusterId}
	
	
		and IMAGE.TYPE=#{type}
	
	
		and IMAGE.LOG_ID=#{logId}
	
	
		and IMAGE.MEMORY_SIZE=#{memorySize}
	
	
		and IMAGE.INSTANCE_NUM=#{instanceNum}
	
	
		and IMAGE.STATUS=#{status}
	
	
		and IMAGE.DELETED=#{deleted}
	
	
		and IMAGE.CREATE_TIME=#{createTime}
	
	
		and IMAGE.UPDATE_TIME=#{updateTime}
	
	
		and IMAGE.CREATE_USER=#{createUser}
	
	
		and IMAGE.UPDATE_USER=#{params.updateUser}

where 1=1

	
		and IMAGE.ID=#{params.id}
	
	
		and IMAGE.VERSION like CONCAT('%',#{params.version},'%') escape '^'
	
	
		and IMAGE.DESCN=#{params.descn}
	
	
		and IMAGE.AREA_ID=#{params.areaId}
	
	
		and IMAGE.HCLUSTER_ID=#{params.hclusterId}
	
	
		and IMAGE.TYPE=#{params.type}
	
	
		and IMAGE.LOG_ID=#{params.logId}
	
	
		and IMAGE.MEMORY_SIZE=#{params.memorySize}
	
	
		and IMAGE.INSTANCE_NUM=#{params.instanceNum}
	
	
		and IMAGE.STATUS=#{params.status}
	
	
		and IMAGE.DELETED=#{params.deleted}
	
	
		and IMAGE.CREATE_TIME=#{params.createTime}
	
	
		and IMAGE.UPDATE_TIME=#{params.updateTime}
	
	
		and IMAGE.CREATE_USER=#{params.createUser}
	
	
		and IMAGE.UPDATE_USER=#{params.updateUser}

mapper.select:
select ,u.USERNAME from EC_GCE_IMAGE IMAGE left join WEBPORTAL_USER u on IMAGE.CREATE_USER = u.ID
ORDER BY IMAGE.UPDATE_TIME DESC
limit #{page.startRowPosition},#{page.recordsPerPage}
