where 1=1

	
		and ID=#{id}
	
	
		and VERSION like CONCAT('%',#{version},'%') escape '^'
	
	
		and DESCN=#{descn}
	
	
		and AREA_ID=#{areaId}
	
	
		and HCLUSTER_ID=#{hclusterId}
	
	
		and TYPE=#{type}
	
	
		and LOG_ID=#{logId}
	
	
		and MEMORY_SIZE=#{memorySize}
	
	
		and INSTANCE_NUM=#{instanceNum}
	
	
		and STATUS=#{status}
	
	
		and DELETED=#{deleted}
	
	
		and CREATE_TIME=#{createTime}
	
	
		and UPDATE_TIME=#{updateTime}
	
	
		and CREATE_USER=#{createUser}
	
	
		and UPDATE_USER=#{params.updateUser}

where 1=1

	
		and ID=#{params.id}
	
	
		and VERSION like CONCAT('%',#{params.version},'%') escape '^'
	
	
		and DESCN=#{params.descn}
	
	
		and AREA_ID=#{params.areaId}
	
	
		and HCLUSTER_ID=#{params.hclusterId}
	
	
		and TYPE=#{params.type}
	
	
		and LOG_ID=#{params.logId}
	
	
		and MEMORY_SIZE=#{params.memorySize}
	
	
		and INSTANCE_NUM=#{params.instanceNum}
	
	
		and STATUS=#{params.status}
	
	
		and DELETED=#{params.deleted}
	
	
		and CREATE_TIME=#{params.createTime}
	
	
		and UPDATE_TIME=#{params.updateTime}
	
	
		and CREATE_USER=#{params.createUser}
	
	
		and UPDATE_USER=#{params.updateUser}
