mapper.update:
	update MATRIX_BILLING_ORDER


            DISCOUNT_PRICE=#{discountPrice},

        UPDATE_TIME=now(),

            UPDATE_USER=#{updateUser}


	where ID=#{orderId}

mapper.sql:
where 1=1


        and od.ID=#{id}


        and od.ORDER_NUMBER like CONCAT('%','${orderNumber}','%') escape '^' 


        and od.SUBSCRIPTION_ID=#{subscriptionId}


        and od.START_TIME=#{startTime}


        and od.END_TIME=#{endTime}


        and od.PRICE=#{price}


        and od.DISCOUNT_PRICE=#{discountPrice}


        and od.STATUS=#{status}


        and od.DELETED=#{deleted}


        and od.CREATE_TIME=#{createTime}


        and od.UPDATE_TIME=#{updateTime}


        and od.CREATE_USER=#{createUser}


        and od.UPDATE_USER=#{updateUser}


        and od.DESCN=#{descn}
